import React from 'react'
import { ImageBackground, StyleSheet, Text, TouchableOpacity, View, Image, TextInput } from 'react-native'
import { useState, useEffect } from 'react'
import bg from '../image/bg1.png'



const padTime = (time) => {
  return String(time).length === 1 ? `0${time}` : `${time}`;
};

const format = (time) => {
  const minutes = Math.floor(time / 60);
  const seconds = time % 60;
  return `${minutes}:${padTime(seconds)}`;
};






export default function Game({ navigation, route }) {
  const{operator, maxNum, level} = route.params
  const [counter, setCounter] = React.useState(700);
  React.useEffect(() => {
    let timer;
    if (counter > 0) {
      timer = setTimeout(() => setCounter((c) => c - 1), 1000);
    }

    return () => {
      if (timer) {
        clearTimeout(timer);

      }
    };
  }, [counter]);

  const [data, setData] = useState([])
  const [num, setNum] = useState(0)
  const [check, setCheck] = useState('')

  let arr = []

let summ 

  if(maxNum === 10 && operator === '+') 
  for (let i = 0; i < data.length; i++) {
    summ+=data[i]
  }
  

if (maxNum === 10 && operator === '*')
  for (let i = 0; i < data.length; i++) {
    summ*=data[i]
  }


  const changeEquation = () => {
    arr = []
    for (let i = 0; i < maxNum; i++) {
      arr.push(Math.floor(Math.random() * 99))
      setData([...arr])
      console.log(arr)
    }
    setNum(0)
  }



  const nextNum = () => {

    num == maxNum ? alert('vse') : setNum(num + 1)
  }
  const proverka = () => {
    if (check == summ) alert('verno')
    else alert('neverno')
  }
  useEffect(() => {
    changeEquation()
  }, [])







  const valueBtn = [
    {
      textNext: 'Next',
      textChek: 'Check',
      textExample: 'Next Example'
    }
  ]




  

  return (

    <View style={styles.game}>
      <ImageBackground source={bg} style={styles.gamePhoto}>
        <View style={styles.textLevel}>
     
          <View className="App">
            <Text>{counter === 0 ? navigation.navigate('Result') : `Timer: ${format(counter)}`}</Text>
          </View>
          <Text>Option {operator}</Text>  
                <Text>{level} level</Text> 
        </View>
        <View>
          <TextInput type="text" value={summ} style={styles.randomNumber} />


        </View>

        {
          num == maxNum ?
            <>
              <TextInput type="text" onChange={e => setCheck(e.target.value)} value={check} style={styles.randomNumber} />
              <TouchableOpacity onPress={() => proverka()} style={styles.next}>
                <Text style={styles.nextText} >
                  Check
                          </Text>
              </TouchableOpacity>
            </>
            :
            <>
              <TextInput style={styles.randomNumber} value={data[num]} />
              <TouchableOpacity onPress={() => nextNum()} style={styles.next}>
                <Text style={styles.nextText} >
                  Next
                </Text>
              </TouchableOpacity>
            </>
        }
      </ImageBackground>
    </View>
  )
}
const styles = StyleSheet.create({
  game: {
    flex: 1,
  },
  gamePhoto: {
    height: '100%',
    width: '100%'
  },
  textLevel: {
    width: '80%',
    flex: 1,
    flexDirection: 'row',
    justifyContent: 'space-around',
    marginTop: '15%',
    marginLeft: '10%'
  },
  plus: {
    width: 45,
    height: 45,

  },
  level: {
    color: '#0186BF',
    fontSize: 35,
  },
  second: {
    color: '#0186BF',
    textAlign: 'center',
    fontSize: 27,
    marginBottom: '5%'
    // marginTop: 80,
  },
  randomNumber: {
    width: 150,
    height: 150,
    backgroundColor: 'rgba(175,212,113,0.2)',
    borderWidth: 1,
    borderColor: 'rgba(175,212,113,1)',
    borderRadius: 25,
    marginBottom: '5%',
    marginLeft: '30%',
  },
  seven: {
    width: 100,
    flex: 1,
    justifyContent: 'center',
    marginLeft: '20%'
  },
  next: {
    // marginTop: 45,
    marginLeft: '10%',
    width: 300,
    height: 40,
    borderRadius: 50,
    backgroundColor: '#AFD471',
    marginBottom: '70%'

  },
  nextText: {
    color: 'white',
    textAlign: 'center',
    fontSize: 25,
  },

})
