import React from 'react'
import { ImageBackground, StyleSheet, Text, TouchableOpacity, View, Image } from 'react-native'
import {useState,useEffect} from 'react'
import bg from '../image/bg1.png'
import plus from '../image/plus.png'
import seven from '../image/seven.png'


const padTime = (time) => {
    return String(time).length === 1 ? `0${time}` : `${time}`;
  };

  const format = (time) => {
    const minutes = Math.floor(time / 60);
    const seconds = time % 60;
    return `${minutes}:${padTime(seconds)}`;
  };


export default function Game({navigation, route}) {
    const{level, operator} = route.params
    const [counter, setCounter] = React.useState(10);
    React.useEffect(() => {
      let timer;
      if (counter > 0) {
        timer = setTimeout(() => setCounter((c) => c - 1), 1000);
      }

      return () => {
        if (timer) {
          clearTimeout(timer);

        }
      };
    }, [counter]);

    return (

        <View style={styles.game}>
        <ImageBackground source={bg} style={styles.gamePhoto}>
        <View style={styles.textLevel}>
            <Text style={styles.level}>Easy level</Text>
            <View className="App">
        <Text>{counter === 0 ?  navigation.navigate('Result') : `Timer: ${format(counter)}`}</Text>
      </View>
                   <Image style={styles.plus} source={plus}/>
        </View>
          <View>
              <Text style={styles.second}>seconds left</Text>
          </View>
                   <View  style={styles.randomNumber}>
                       <Image source={seven} style={styles.seven}/>

                   </View>
            <TouchableOpacity style={styles.next} 
            onPress={()=>
                navigation.navigate('Answer')
            }>
        <Text style={styles.nextText}>NEXT</Text>
            </TouchableOpacity>

            </ImageBackground>
        </View>
    )
}
const styles = StyleSheet.create({
    game: {
        flex: 1,
    },
    gamePhoto: {
        height: '100%',
        width: '100%'
    },
    textLevel: {
        width: '80%',
        flex: 1,
        flexDirection: 'row',
        justifyContent: 'space-around', 
        marginTop: '15%',
        marginLeft: '10%'  
    },
    plus: {
        width: 45,
        height: 45,

    },
    level: {
       color: '#0186BF',
       fontSize: 35,  
    },
    second: {
        color: '#0186BF',
        textAlign: 'center',
        fontSize: 27,
        marginBottom: '5%'
        // marginTop: 80,
     },
     randomNumber: {
        width: 150,
        height: 150,
        backgroundColor: 'rgba(175,212,113,0.2)',
        borderWidth: 1,
        borderColor: 'rgba(175,212,113,1)',
        borderRadius: 25,
        marginBottom: '5%',
        marginLeft: '30%',
    },
    seven: {
        width: 100,
        flex: 1,
        justifyContent: 'center',
        marginLeft: '20%'
    },
      next: {
        // marginTop: 45,
        marginLeft: '10%',
        width: 300,
        height: 40, 
       borderRadius: 50,
       backgroundColor: '#AFD471',
       marginBottom: '70%'

    },
    nextText: {
        color: 'white',
        textAlign: 'center',
        fontSize: 25,
     },

})
